import React, { useState, useEffect, useRef, useMemo } from 'react';
import maplibregl from 'maplibre-gl';
import 'maplibre-gl/dist/maplibre-gl.css';
import { GoogleGenerativeAI } from '@google/generative-ai';

// --- LLM API Setup ---
const genAI = new GoogleGenerativeAI(process.env.REACT_APP_GEMINI_API_KEY);

// --- START: NEW INTERACTIVE DESCRIPTION COMPONENT ---
// (You can move this to its own file and import it if you prefer)
const InteractiveDescription = ({ text, keywords, colors, onKeywordClick }) => {
  const parts = useMemo(() => {
    if (!text || !keywords.length) {
      return [text];
    }
    const regex = new RegExp(`(${keywords.join('|')})`, 'gi');
    return text.split(regex).filter(part => part !== '');
  }, [text, keywords]);

  return (
    <p style={{ fontSize: '0.95rem', color: '#6c757d', lineHeight: 1.6 }}>
      {parts.map((part, index) => {
        const originalKeyword = keywords.find(kw => kw.toLowerCase() === part.toLowerCase());
        if (originalKeyword) {
          return (
            <strong
              key={index}
              style={{
                color: colors[originalKeyword] || '#000',
                cursor: 'pointer',
                fontWeight: 'bold',
                textDecoration: 'underline',
                textDecorationColor: colors[originalKeyword] || '#000'
              }}
              onClick={() => onKeywordClick(originalKeyword)}
            >
              {part}
            </strong>
          );
        }
        return <React.Fragment key={index}>{part}</React.Fragment>;
      })}
    </p>
  );
};
// --- END: NEW INTERACTIVE DESCRIPTION COMPONENT ---


// --- Legend Component (Unchanged) ---
const Legend = ({ title, items }) => {
  // ... (legend component code is unchanged)
};


// --- Map Component ---
export default function Map() {
  const mapContainer = useRef(null);
  const map = useRef(null);
  const descriptionCache = useRef({});

  // --- ADDED: Definitions for interactive text highlighting ---
  const PRECINCT_NAMES = ['Arden', 'Macaulay', 'Fishermans Bend', 'Docklands', 'City North'];
  const PRECINCT_COLORS = {
    'Arden': '#3498db',           // Blue
    'Macaulay': '#e74c3c',         // Red
    'Fishermans Bend': '#2ecc71', // Green
    'Docklands': '#f39c12',         // Orange
    'City North': '#9b59b6'       // Purple
  };

  // State
  const [searchText, setSearchText] = useState('');
  const [indicators, setIndicators] = useState([]);
  const [selectedIndicator, setSelectedIndicator] = useState(null);
  const [showIndicators, setShowIndicators] = useState(false);
  const [panelFocus, setPanelFocus] = useState(null);
  const [dynamicDescription, setDynamicDescription] = useState('');
  const [isDescriptionLoading, setIsDescriptionLoading] = useState(false);
  // --- ADDED: State to track precinct highlighted from the text panel ---
  const [textHoveredPrecinct, setTextHoveredPrecinct] = useState(null);

  // Panel widths
  const leftPanelWidth = 288;
  const rightPanelWidth = 175;

  // Data objects (legendData and indicatorConfig)
  const legendData = { /* ... (no changes) ... */ };
  const indicatorConfig = { /* ... (no changes from last correct version) ... */ };

  // --- Main Map Initialization useEffect ---
  useEffect(() => {
    // ... (This entire useEffect block for map init is unchanged from the last correct version)
    // It sets up sources, layers, and base click handlers.
  }, []);

  // ... (adjustMapBounds and resize listener useEffects are unchanged) ...

  // --- Layer Visibility useEffect (Unchanged) ---
  useEffect(() => {
     // ... (This useEffect for toggling indicator layers is unchanged) ...
  }, [selectedIndicator]);

  // --- LLM Description useEffect (Unchanged) ---
  useEffect(() => {
    // ... (The main useEffect for generating descriptions via the LLM is unchanged) ...
  }, [panelFocus]);

  // --- ADDED: useEffect to visually update map when a precinct is highlighted from text ---
  useEffect(() => {
    if (!map.current || !map.current.isStyleLoaded()) return;

    // This expression makes the highlighted precinct stand out and fades the others.
    const fillOpacity = textHoveredPrecinct
      ? ['case', ['==', ['get', 'name'], textHoveredPrecinct], 0.6, 0.1]
      : 0.15; // Revert to default opacity if no precinct is highlighted

    map.current.setPaintProperty('precincts-fill-layer', 'fill-opacity', fillOpacity);
  }, [textHoveredPrecinct]);


  // --- UI Handlers ---
  const handleSearchClick = () => {
    setIndicators(['Employment', 'Education', 'Income', 'Occupation']);
    setShowIndicators(true);
  };

  const handleIndicatorSelect = (indicator) => {
    const isCurrentlySelected = selectedIndicator === indicator;
    const newIndicator = isCurrentlySelected ? null : indicator;
    setSelectedIndicator(newIndicator);
    setPanelFocus(newIndicator ? { type: 'indicator', name: newIndicator } : null);
    // --- ADDED: Clear text highlight when a new indicator is chosen ---
    setTextHoveredPrecinct(null);
  };

  // --- ADDED: Handler for when a precinct name is clicked in the text ---
  const handlePrecinctHighlight = (precinctName) => {
    // Toggle the highlight: if same name is clicked, turn it off. Otherwise, set it.
    setTextHoveredPrecinct(prev => (prev === precinctName ? null : precinctName));
  };


  return (
    <div style={{ display: 'flex', width: '100%', height: 'calc(100vh - 78px)' }}>
      {/* Map container */}
      <div style={{ position: 'relative', flex: 1 }}>
        {/* ... (mapContainer div and left panel UI are unchanged) ... */}
      </div>

      {/* Text Explorer Panel --- MODIFIED --- */}
      <div style={{ width: '320px', backgroundColor: '#f8f9fa', padding: '1.5rem', borderLeft: '1px solid #dee2e6', overflowY: 'auto' }}>
        {panelFocus ? (
          <div>
            <h4 style={{ fontSize: '1.1rem', fontWeight: 600, color: '#495057', marginBottom: '0.75rem' }}>{panelFocus.name}</h4>
            {isDescriptionLoading ? (
              <p style={{ fontSize: '0.95rem', color: '#6c757d', fontStyle: 'italic' }}>ðŸ¤– Generating AI description...</p>
            ) : (
              // --- MODIFIED: Use the new InteractiveDescription component ---
              <InteractiveDescription
                text={dynamicDescription}
                keywords={PRECINCT_NAMES}
                colors={PRECINCT_COLORS}
                onKeywordClick={handlePrecinctHighlight}
              />
            )}
          </div>
        ) : (
          <p style={{ fontSize: '0.95rem', color: '#6c757d', fontStyle: 'italic' }}>Select an indicator from the left panel or click on a precinct on the map to see its description.</p>
        )}
      </div>
    </div>
  );
}
